$(document).ready(function () {
  $('.bq1').click(function () {
    $('#q1').fadeOut('slow', function () {
      $('#q2').fadeIn('slow');
    });
  });

  $('.bq2').click(function () {
    $('#q2').fadeOut('slow', function () {
      $('#q3').fadeIn('slow');
    });
  });

  $('.bq3').click(function () {
    $('#q3').fadeOut('slow', function () {
      $('#q4').fadeIn('slow');
    });
  });

  $('.bq4').click(function () {
    scrollTo('id1');
    $('#content1, #advan').fadeOut('slow', function () {
      $('#content2').fadeIn(),
        setTimeout(function () {
          $('#result').fadeIn(1000); // Появление #result1 с задержкой 1000 мс (1 секунда)
        }, 1200);

      setTimeout(function () {
        $('#result1').fadeIn(1000); // Появление #result1 с задержкой 1000 мс (1 секунда)
      }, 2100);

      setTimeout(function () {
        $('#result2').fadeIn(1000); // Появление #result2 с задержкой 3000 мс (3 секунды) от начала фейд-ин анимации
      }, 3100);

      setTimeout(function () {
        $('#content2').fadeOut('slow', function () {
          if (typeof roulette_ini !== 'undefined') {
            rouletteRoot._init();
          } else if (typeof box_ini !== 'undefined') {
            boxRoot._init();
          }
          $('#content3').fadeIn(function () {
            this.style.setProperty('display', 'flex', 'important');
            this.style.setProperty('justify-content', 'center', 'important');
            this.style.setProperty('align-items', 'center', 'important');
          });
        });
      }, 4000);
    });
  });
});
