    promoSectionStatus: false,
    redirect: false,
    GTM: false,
    get promoSection() {
        return this.promoSectionStatus
    },
    set promoSection(value) {
        if (typeof value !== "boolean") {
            throw "Неверное значение! Должно быть true или false";
            return undefined
        }
        this.promoSectionStatus = value;
        promoSectionStatusActivate(this.promoSectionStatus)
    }
};

function promoSectionStatusActivate(status) {
    let sections = document.querySelectorAll("section.promo-section");
    if (!sections) {
        return
    }
    for (let element of sections) {
        if (status) {
            element.style.display = "flex"
        } else {
            element.style.display = "none"
        }
        return
    }
}
