window.countryData = {
    valueCode: undefined,
    valueName: undefined,
    valuePhoneCode: undefined,
    fnCode: [],
    fnName: [],
    fnPhoneCode: [],
    get code() {
        return this.valueCode
    },
    set code(val) {
        this.valueCode = val;
        activeCountryDataFn(window.countryData.fnCode)
    },
    get name() {
        return this.valueName
    },
    set name(val) {
        this.valueName = val;
        activeCountryDataFn(window.countryData.fnName)
    },
    get phoneCode() {
        return this.valuePhoneCode
    },
    set phoneCode(val) {
        this.valuePhoneCode = val;
        activeCountryDataFn(window.countryData.fnPhoneCode)
    }
};

function getCountryData() {
    !window.isLocalHost && fetch(`${window.apiUrl}/v6/platform/values`, {
        method: "POST",
        body: JSON.stringify({
            list: ["country_data"]
        }),
        headers: {
            "X-Request-Project": "otp",
            "X-Request-Type": "Api-Request",
            "X-Requested-With": "XMLHttpRequest"
        }
    }).then(data => data.text()).then(data => {
        let info = JSON.parse(data).data.country_data;
        window.countryData.code = info.code;
        window.countryData.name = info.name;
        window.countryData.phoneCode = info.phone_code;
        activeCountryDataFn(window.countryData.fnCode);
        activeCountryDataFn(window.countryData.fnName);
        activeCountryDataFn(window.countryData.fnPhoneCode)
    })["catch"](e => {
        console.error("GetCountryData: " + "Ошибка при определеннии страны пользователя");
        activeCountryDataFn(window.countryData.fnCode);
        activeCountryDataFn(window.countryData.fnName);
        activeCountryDataFn(window.countryData.fnPhoneCode)
    })
}

function activeCountryDataFn(fn) {
    if (typeof fn !== "object" && fn.length === 0) {
        return
    }
    for (let key of fn) {
        if (typeof key === "function") {
            key()
        }
    }
}
getCountryData();
