        }
    });

    function startCheckLogin(elem) {
        const attrVal = String(elem.getAttribute("data-checklogin").toLowerCase());
        if (attrVal === "" || attrVal === "reg") {
            checkLoginGo()
        } else if (attrVal === "login") {
            checkLoginGo(true)
