    "use strict";
document.addEventListener("DOMContentLoaded", function () {

  let stateOnboard = {
    submitActive: true,
    step: 1,
    all: document.querySelectorAll(".onboarding-component__step").length
  }

  const onboardingSection = document.querySelector(".onboarding-component");

  let allSubmitOnboardingNextInterface = document.querySelectorAll(".onboarding-component__next-step-interface");
  let allSubmitOnboardingNext = document.querySelectorAll(".onboarding-component__next-step");
  let allSubmitOnboardingPrev = document.querySelectorAll(".onboarding-component__prev-step");
  let allElementInterface = document.querySelectorAll(".onboarding-component__element-interface");


  allSubmitOnboardingNextInterface.forEach(
    function(submitOnboardingNextInterface){
      submitOnboardingNextInterface.addEventListener("click", ()=>{ nextOnboarding() });
    }
  );
  allSubmitOnboardingNext.forEach(
    function(submitOnboardingNext){
      submitOnboardingNext.addEventListener("click", ()=>{ nextOnboarding() });
    }
  );
  allSubmitOnboardingPrev.forEach(
    function(submitOnboardingPrev){
      submitOnboardingPrev.addEventListener("click", ()=>{ nextOnboarding(false) });
    }
  );

  function nextOnboarding(next=true, animSpeed = 0.5){
    if (next && stateOnboard.step >= stateOnboard.all){
      let formRegLogComponent = document.querySelector(".block__form-reg-login-update");
      if(formRegLogComponent) {
        if (formRegLogComponent.querySelector('.forms_sidebar')) {
            openFormSidebar();
        } else {
            smoothScroll(formRegLogComponent, 100);
        }
      }
      return false;
    }
    if(!stateOnboard.submitActive) return false;


    let elementSelected = document.querySelector(`.onboarding-component__step:nth-of-type(${stateOnboard.step})`);

    if(next){
      stateOnboard.step = stateOnboard.step + 1;
    }else{
      stateOnboard.step = stateOnboard.step - 1;
    }

    let elementSelectedNext = document.querySelector(`.onboarding-component__step:nth-of-type(${stateOnboard.step})`);
    let elementSelectedNextModal = document.querySelector(`.onboarding-component__step:nth-of-type(${stateOnboard.step}) .onboarding-component__modal-block`);

    elementSelectedNext.style.animationName = "onboarding-step-show";
    elementSelectedNext.style.animationDuration = `${animSpeed}s`;
    elementSelectedNextModal.style.animationDuration = `${animSpeed}s`;
    elementSelectedNext.style.display = "block";    

    elementSelected.style.animationName = "onboarding-step-hidde";
    elementSelected.style.animationDuration = `${animSpeed}s`;

    stateOnboard.submitActive = false
    setTimeout(()=>{
      elementSelected.style.display = "none";
      stateOnboard.submitActive = true;
    }, animSpeed*1000)
  }

    setGTMButtons ();

  function setGTMButtons (){
      const allElementsNextStep = onboardingSection.querySelectorAll(".onboarding-component__next-step");
      const allElementsPrevStep = onboardingSection.querySelectorAll(".onboarding-component__prev-step");
      const allElementsInterfaceNextStep = onboardingSection.querySelectorAll(".onboarding-component__next-step-interface");

      const allSubmitGTM = {};

      for (let elem of allElementsNextStep){
          const step = elem.closest(".onboarding-component__step").getAttribute('data-stepOB');
          allSubmitGTM[`slide-${step}/submit_next-step`] = elem;
      }
      for (let elem of allElementsPrevStep){
          const step = elem.closest(".onboarding-component__step").getAttribute('data-stepOB');
          allSubmitGTM[`slide-${step}/submit_prev-step`] = elem;
      }
      for (let elem of allElementsInterfaceNextStep){
          const step = elem.closest(".onboarding-component__step").getAttribute('data-stepOB');
          allSubmitGTM[`slide-${step}/interface`] = elem.closest(".onboarding-component__element-interface");
      }
      GTMClickHandler({
          location: "onboarding",
          interactiveElements: allSubmitGTM,
      });
  }

});

