        function validateForm(options) {
  const form = document.querySelector(`#${options.formId}`);
  const inputs = [...form.querySelectorAll("input")];
  const submitBtn = form.querySelector(".form--submit");
  let regexpDomen = /@[a-z0-9-]+\.[a-z0-9-]+/;
  let regexpEmailLetters = /^[a-zA-Z0-9.+_/@?/-]+$/;
  let regexpPassSymbols = /^[a-zA-Z0-9.!;:'"()*+=?<>[\]/.//\\/^_`{|}~@#$%&-]+$/;
  let typeOfForm = options.loginForm ? "loginForm" : "regaForm";

  function validateEmail(element) {
    let emailLength = Number(element.value.length) <= 0;
    let emailDomen = !element.value.match(regexpDomen);
    let lettersMatch = !element.value.match(regexpEmailLetters);
    // check letters before "@"
    if (element.value.length !== 0 && lettersMatch) {
      element.parentElement
        .querySelector(".error-message")
        .classList.toggle(options.errorMessageEmailPatternClass, lettersMatch);
      element.classList.toggle(options.inputErrorClass, lettersMatch);
    } else if (
      element.parentElement.querySelector(".error-message").classList.contains(options.errorMessageEmailPatternClass)
    ) {
      element.parentElement.querySelector(".error-message").classList.remove(options.errorMessageEmailPatternClass);
      element.classList.remove(options.inputErrorClass);
    }
    if (element.value.length !== 0 && emailDomen) {
      element.parentElement
        .querySelector(".error-message")
        .classList.toggle(options.errorMessageEmailCorrectClass, emailDomen);
      element.classList.toggle(options.inputErrorClass, emailDomen);
    } else if (
      element.parentElement.querySelector(".error-message").classList.contains(options.errorMessageEmailCorrectClass)
    ) {
      element.parentElement.querySelector(".error-message").classList.remove(options.errorMessageEmailCorrectClass);
      element.classList.remove(options.inputErrorClass);
    }
    if (element.value.length === 0 && emailLength) {
      element.parentElement
        .querySelector(".error-message")
        .classList.toggle(options.errorMessageEmailEmptyClass, emailLength);
      element.classList.toggle(options.inputErrorClass, emailLength);
    } else if (
      element.parentElement.querySelector(".error-message").classList.contains(options.errorMessageEmailEmptyClass)
    ) {
      element.parentElement.querySelector(".error-message").classList.remove(options.errorMessageEmailEmptyClass);
      element.classList.remove(options.inputErrorClass);
    }
    if (!emailLength && !emailDomen && !lettersMatch && !options.loginForm) {
      element.parentElement.querySelector(".success-alert").classList.add(options.inputSuccessClass);
      element.classList.add("input--valid");
    } else {
      element.parentElement.querySelector(".success-alert").classList.remove(options.inputSuccessClass);
      element.classList.remove("input--valid");
    }
    if (emailLength || emailDomen) {
      checkErrorForm(`${typeOfForm}_incorrect_email`);
    }
  }

  function validatePassword(element) {
    let numberMinMatch = Number(element.value.length) <= Number(element.dataset.minlength - 1);
    let emptyValueMatch = Number(element.value.length) < 1;
    let passwordMatch = !element.value.match(regexpPassSymbols);
    if (element.value.length === 0) {
      element.parentElement.querySelector(".error-message").classList.add(options.errorMessagePasswordEmpty);
      element.classList.add(options.inputErrorClass);
    } else if (
      element.parentElement.querySelector(".error-message").classList.contains(options.errorMessagePasswordEmpty)
    ) {
      element.parentElement.querySelector(".error-message").classList.remove(options.errorMessagePasswordEmpty);
      element.classList.remove(options.inputErrorClass);
    }
    if (element.value.length !== 0 && numberMinMatch) {
      element.parentElement
        .querySelector(".error-message")
        .classList.toggle(options.errorMessageLengthPasswordClass, numberMinMatch);
      element.classList.toggle(options.inputErrorClass, numberMinMatch);
    } else if (
      element.parentElement.querySelector(".error-message").classList.contains(options.errorMessageLengthPasswordClass)
    ) {
      element.parentElement.querySelector(".error-message").classList.remove(options.errorMessageLengthPasswordClass);
      element.classList.remove(options.inputErrorClass);
    }
    if (element.value.length !== 0 && passwordMatch) {
      element.parentElement
        .querySelector(".error-message")
        .classList.toggle(options.errorMessagePasswordSymbols, passwordMatch);
      element.classList.toggle(options.inputErrorClass, passwordMatch);
    } else if (
      element.parentElement.querySelector(".error-message").classList.contains(options.errorMessagePasswordSymbols)
    ) {
      element.parentElement.querySelector(".error-message").classList.remove(options.errorMessagePasswordSymbols);
      element.classList.remove(options.inputErrorClass);
    }
    if (!options.loginForm && !numberMinMatch && !emptyValueMatch && !passwordMatch) {
      element.parentElement.querySelector(".success-alert").classList.add(options.inputSuccessClass);
      element.classList.add("input--valid");
    } else {
      element.parentElement.querySelector(".success-alert").classList.remove(options.inputSuccessClass);
      element.classList.remove("input--valid");
    }
    if (numberMinMatch || emptyValueMatch) {
      checkErrorForm(`${typeOfForm}_incorrect_password`);
    }
  }

  function removeClassInputError(element, nameClass, elCheckbox) {
    if (element.parentElement.querySelector(".error-message").classList.contains(nameClass)) {
      element.parentElement.querySelector(".error-message").classList.remove(nameClass);
      element.classList.remove(options.inputErrorClass);
    }
    if (elCheckbox === true) {
      element.parentElement.querySelector(".register-form__agreement-checkbox").classList.remove("input_error");
    }
  }

  function validateCheckbox(element) {
    let checkboxValue = !element.checked;
    element.parentElement.querySelector(".error-message").classList.toggle(options.errorMessageClass, checkboxValue);
    element.parentElement
      .querySelector(".register-form__agreement-input")
      .classList.toggle(options.inputErrorClass, checkboxValue);
    element.parentElement
      .querySelector(".register-form__agreement-checkbox")
      .classList.toggle(options.inputErrorClass, checkboxValue);
    if (checkboxValue) {
      checkErrorForm(`${typeOfForm}_incorrect_checkbox`);
    }
  }

  function validateEmailLetters(element) {
    let lettersMatch = !element.value.match(regexpEmailLetters);
    // check letters before "@"
    if (element.value.length !== 0 && lettersMatch) {
      element.parentElement
        .querySelector(".error-message")
        .classList.toggle(options.errorMessageEmailPatternClass, lettersMatch);
      element.classList.toggle(options.inputErrorClass, lettersMatch);
    } else if (
      element.parentElement.querySelector(".error-message").classList.contains(options.errorMessageEmailPatternClass)
    ) {
      element.parentElement.querySelector(".error-message").classList.remove(options.errorMessageEmailPatternClass);
      element.classList.remove(options.inputErrorClass);
    }
  }

  function validateInput() {
    switch (this.dataset.type) {
      case "email":
        validateEmail(this);
        break;
      case "password":
        validatePassword(this);
        break;
      case "checkbox":
        validateCheckbox(this);
        break;
      default:
        return false;
    }
  }
  inputs.forEach(function (el) {
    if (el.dataset.type !== "checkbox") {
      el.addEventListener(
        "keyup",
        function () {
          switch (this.dataset.type) {
            case "email":
              validateEmailLetters(this);
              let lettersMatch = !this.value.match(regexpEmailLetters);
              let emailDomen = !this.value.match(regexpDomen);
              if (this.value.length > 0 && !emailDomen && !lettersMatch && !options.loginForm) {
                this.parentElement.querySelector(".success-alert").classList.add(options.inputSuccessClass);
                this.classList.add("input--valid");
              } else {
                this.parentElement.querySelector(".success-alert").classList.remove(options.inputSuccessClass);
                this.classList.remove("input--valid");
              }
              break;
            case "password":
              if (!!this.value.match(regexpPassSymbols) && this.value.length >= 4 && !options.loginForm) {
                this.parentElement.querySelector(".success-alert").classList.add(options.inputSuccessClass);
                this.classList.add("input--valid");
              } else {
                this.parentElement.querySelector(".success-alert").classList.remove(options.inputSuccessClass);
                this.classList.remove("input--valid");
              }
              break;
            default:
              return false;
          }
        },
        true
      );
      el.addEventListener(
        "focus",
        function () {
          switch (this.dataset.type) {
            case "email":
              removeClassInputError(this, options.errorMessageEmailCorrectClass);
              removeClassInputError(this, options.errorMessageEmailEmptyClass);
              removeClassInputError(this, options.errorMessageEmailPatternClass);
              break;
            case "password":
              removeClassInputError(this, options.errorMessagePasswordEmpty);
              removeClassInputError(this, options.errorMessageLengthPasswordClass);
              removeClassInputError(this, options.errorMessagePasswordSymbols);
              break;
            default:
              return false;
          }
        },
        false
      );
    } else {
      el.addEventListener(
        "change",
        function () {
          if (el.dataset.required === "required") {
            removeClassInputError(this, options.errorMessageClass, true);
          }
        },
        true
      );
    }
  });

  function submitFields() {
    const isEmpty = (elem) => elem.value.length === 0 || elem.checked === false;
    const form = document.querySelector(`#${options.formId}`);
    if (inputs.some(isEmpty)) {
      inputs.forEach((el) => {
        if (el.dataset.required === "required") {
          validateInput.call(el);
        }
      });
    }
    const isNotValid = (elem) => elem.classList.contains(options.inputErrorClass);
    form.classList.toggle(options.formInvalidClass, inputs.some(isNotValid));
  }
  submitBtn.addEventListener("click", submitFields, true);
}

function validateServerError(
  elServerClass,
  elServerClassActive,
  elInput,
  timerErrorHide,
  msgServerError,
  blockMsgServerError
) {
  if (msgServerError) {
    document.querySelector(elInput).parentElement.querySelector(blockMsgServerError).innerHTML = msgServerError;
  }
  document.querySelector(elServerClass).classList.add(elServerClassActive);
  document.querySelector(elInput).classList.remove("input--valid");
  document
    .querySelector(elInput)
    .parentElement.querySelector(".success-alert")
    .classList.remove("success-alert--active");
  setTimeout(function () {
    document.querySelector(elServerClass).classList.remove(elServerClassActive);
  }, timerErrorHide);
}

