let account_islamic = false;

// <<< SEND SERVER REQUEST >>>
const form_rega = (email, password, project, link, account_islamic, token) => {
    const timezone = (new Date().getTimezoneOffset() / 60) * -1;
    const params = {
        data: {
            email,
            password,
            timezone,
            project: project.toLowerCase(),
                            },
        list: ["data", "auth_token"]
    };
    if (window.captchaRegistration) {
        const captcha = {
            captcha: {
                type: "recaptcha",
                data: {
                    token
                }
            }
        };
        Object.assign(params, captcha);
    }

    if (typeof window.deepLink !== 'undefined' && window.deepLink.indexOf('tutorialScenario') !== -1) {
        const tutorialScenario_value = window.deepLink.substring(window.deepLink.indexOf("tutorialScenario=")).replace('tutorialScenario=', '').split('&')[0];
        params['tutorial_scenario'] = tutorialScenario_value;
    }

    window.userParams = params;
    const urlApiRegistration = `${window.apiUrl}/v1/user/registration-by-password`;

    function getLink() {
        const host = window.platformUrl;
        const landName = window.location.pathname.split('/')[2];
        let ref = 'olymp';

        const params = window.location.search
            .replace('?', '').split('&')
            .reduce(
                function(p, e) {
                    const a = e.split('=');
                    if (a[0] === 'ref') {
                        ref = a[1];
                    }
                    if (a[0] === 'rf') {
                        p += `refer=${a[1]}&`;
                    } else {
                        p += `${e}&`;
                    }
                    return p;
                },
                ''
            );
        const search = `l/${landName}/${ref}?${params}`;
        return host + search;
    }

    const linkPixelUrl = `${getLink()}jwt_traffic=1`;

    fetch(linkPixelUrl).then(function(response) {
        return response.text();
    }).then(function(token) {
        const additionalUrlParam = `?jwt_traffic=${token}`;

        fetch(urlApiRegistration + additionalUrlParam, {
            method: 'POST',
            body: JSON.stringify(params),
            headers: {
                'Content-Type': 'application/json; charset=UTF-8',
                'X-Request-Type': 'Api-Request',
                'X-Request-Project': project
            },
            credentials: 'include'
        }).then(response => {
            if (response.ok) {
                response.json().then(response => {
                    const authToken = response.data.auth_token.token;
                    let separator = '?';
                    if (link.includes('?')) {
                        separator = '&';
                    }
                    const isSidebarOpen = document.querySelector('.form-sidebar--active');
                    isSidebarOpen ? successForm('registration_platform', 'sidebar_reg') : successForm('registration_platform', 'registration');
                    const redirectLink = `${link}${separator}auth_token=${authToken}`;
                    window.location.replace(redirectLink);
                });
            } else {
                response.json().then(response => {
                    if (response.errors[0]['code'] === 'user_already_authorized') {
                        alert(response.errors[0]['msg']);
                        window.location = link;
                        checkErrorForm('regaForm_server_error_User_already_authorized');
                    }
                    if (response.errors[0]['code'] === 'email_exists') {
                        validateServerError('.server-error-email', 'server-error-email--active', '.register-form__email__input', timerErrorHide);
                        checkErrorForm('regaForm_server_error_Email_exists');
                    }
                });
            }
        });
    }).catch(function(error) {
        console.log('Request failed', error);
    });

    if (window.optionUserValues) {
        params['options'] = window.optionUserValues;
    }
    if (window.hardware_id) {
        params.data['hardware_id'] = window.hardware_id;
    }

};

