    const form_auth = (email, password, token, redirect) => {
        let formData = {
            "data": {
                "email": email,
                "password": password
            }
        };

        if (window.captchaLogin) {
            let formCaptcha = {
                "captcha": {
                    "type": "recaptcha",
                    "data": {
                        "token": token
                    }
                }
            };
            formData = Object.assign(formData, formCaptcha);
        }

        const user_data = JSON.stringify(formData);
        const url = `${window.apiUrl}/v1/user/login-by-password`;

        fetch(url, {
            method: 'POST',
            mode: 'cors',
            cache: 'no-cache',
            credentials: 'include',
            headers: {
                'Content-Type': 'application/json; charset=UTF-8',
                'X-Request-Type': 'Api-Request',
                'X-Request-Project': 'otp'
            },
            redirect: 'follow',
            referrer: 'no-referrer',
            body: user_data,
        }).then(response => {
            setLangToPlatform();
            if (response.ok) {
                window.formLoginGTM_1.reportLandLoginGTM(1);
                successForm('login_platform', 'login');
                window.location = redirect;
            } else {
                response.json().then(response => {
                    if (response.errors[0]['code'] === 'incorrect_password') {
                        validateServerError('.server-error-passw', 'server-error-passw--active', '.login-form__input--pwd', timerErrorHide);
                        checkErrorForm('loginForm_server_error_incorrect_Password');
                    }
                    if (response.errors[0]['code'] === 'user_already_authorized') {
                        successForm('user_already_authorized', 'login');
                        window.location = redirect;
                    }
                    if (response.errors[0]['code'] === 'wrong_captcha') {
                        validateServerError('.server-error-recaptcha', 'server-error-message-recaptcha--active', '.server-error-recaptcha', timerErrorHide);
                        checkErrorForm('loginForm_server_error_wrong_Captcha');
                    }
                    if (response.errors[0]['code'] === 'invalid_email') {
                        validateServerError('.server-error-email', 'server-error-email--active', '.login-form__input--email', timerErrorHide);
                        checkErrorForm('loginForm_server_error_incorrect_Email');
                    }
                    if (response.errors[0]['code'] === 'account_auth_blocked') {
                        document.querySelector('.block-message').innerHTML = response.errors[0]['msg'];
                        validateServerError('.server-error-block', 'server-error-block--active', '.server-error-block', timerErrorHide);
                        checkErrorForm('loginForm_server_error_Account_auth_blocked');
                    }
                })
            }
        });
    };

