        el.forEach((element) => {
            if (element.dataset.hiddenform === data) {
                element.classList.remove('forms__control--active')
            }
        })
    }

    document.querySelectorAll(".show-password").forEach(function(btn) {
        btn.addEventListener('click', function() {
            let input_show = this.dataset.show;
            const passwordInput = document.querySelector(input_show);
            if (passwordInput.type === "password") {
                passwordInput.type = "text";
                this.classList.add('password--active');
            } else {
                passwordInput.type = "password";
                this.classList.remove('password--active');
            }
        });
    });

    controlsBtn.forEach((element) => {
        element.onclick = function() {
            let idFormShow = element.dataset.showform;
            let idFormHidden = element.dataset.hiddenform;
            document.querySelector(idFormShow).classList.remove('block-hidden');
            document.querySelector(idFormHidden).classList.add('block-hidden');
            if (!element.classList.contains('forms__control--active')) {
                element.parentNode.querySelector('.forms__controls__line--active').classList.toggle('forms__controls__line--active--active');
            }
            element.classList.add('forms__control--active');
            removeClassActiveControls(controlsBtn, idFormShow);
        }
    });

