const checkIsCookiesAccepted = () => {
    const isAcceptedByLocalStorage = localStorage.getItem('accept') ? JSON.parse(localStorage.getItem('accept')) : false;
    const isAcceptedByCookie = document.cookie.indexOf('@prm/notifications={"cookieConsentEnabled":0}') > -1;

    return isAcceptedByCookie || isAcceptedByLocalStorage;
}
const loadCookiePopupScripts = (popup) => {
    const closeButton = popup.querySelector('.close-btn')
    const acceptButton = popup.querySelector('.accept-btn');
    const cookieLinkElement = popup.querySelector('#cookie-link');

    function getPlarformLocale() {
        let platformLocale = '';
        if (window.platformGeoCode && window.platformGeoCode != "en_US") {
            platformLocale = `${window.platformGeoCode.toLowerCase()}/`;
            platformLocale = platformLocale.replace(/_/g, '-');
            if (window.platformGeoCode === "zh_CN") {
                platformLocale = `pages/`;
            }
        }
        return platformLocale;
    };

    function closePopup() {
        popup.style.display = 'none';
        localStorage.setItem('accept', JSON.stringify(true));
    };

    closeButton.addEventListener('click', () => {
        closePopup();
    });

    acceptButton.addEventListener('click', () => {
        closePopup();
    })

    cookieLinkElement.href = `${window.platformUrl}${getPlarformLocale()}terms`;
}

const popup = document.getElementById("cookies-popup");
checkIsCookiesAccepted() ? popup.remove() : loadCookiePopupScripts(popup);

